INSERT INTO livro (titulo, autor, paginas, genero, nacional, capa, editora, descricao, id_usuario)
VALUES ('Vidas Secas', 'Graciliano Ramos', 176, 'Romance', 1, '', 'Record', 'Vidas Secas é um profundo retrato da sociedade brasileira, sobretudo de seus problemas sociais. Dessa forma, Graciliano traça uma crítica social retratando as dificuldades encontradas por uma família pobre de retirantes. Eles tem de conviver constantemente com a miséria e a seca que assola o sertão nordestino.', 1);

INSERT INTO livro (titulo, autor, paginas, genero, nacional, capa, editora, descricao, id_usuario)
VALUES ('Quincas Borba', 'Machado de Assis', 240, 'Romance', 1, '', 'Principis', 'Quincas Borba é um romance realista de Machado de Assis. Conta a história de Rubião, um professor de Barbacena que herda os bens de seu amigo Quincas Borba, com a condição de cuidar do cão do falecido, que possui o mesmo nome do dono, ou seja, Quincas Borba.', 1);

INSERT INTO livro (titulo, autor, paginas, genero, nacional, capa, editora, descricao, id_usuario)
VALUES ('A Cabana', 'Wilian P Young', 240, 'Ficcao', 0, '', 'Arqueiro', 'Durante uma viagem de fim de semana, a filha mais nova de Mack Allen Phillips é raptada e evidências de que ela foi brutalmente assassinada são encontradas numa velha cabana.', 1);

INSERT INTO livro (titulo, autor, paginas, genero, nacional, capa, editora, descricao, id_usuario)
VALUES ('Steve Jobs', 'Walte Issacson', 624, 'Biografia', 0, '', 'Companhia das Letras', 'O livro, baseado em mais de quarenta entrevistas com Jobs ao longo de dois anos - e entrevistas com mais de cem familiares, amigos, colegas, adversários e concorrentes -, narra a vida atribulada do empresário extremamente inventivo e de personalidade forte e polêmica,', 1);

INSERT INTO livro (titulo, autor, paginas, genero, nacional, capa, editora, descricao, id_usuario)
VALUES ('A Menina que Roubava Livros', 'Marcus Zusak', 480, 'Romance', 0, '', 'Intrinseca', 'A trajetória de Liesel Meminger é contada por uma narradora mórbida, surpreendentemente simpática. Ao perceber que a pequena ladra de livros lhe escapa, a Morte afeiçoa-se à menina e rastreia suas pegadas de 1939 a 1943', 1);

INSERT INTO livro (titulo, autor, paginas, genero, nacional, capa, editora, descricao, id_usuario)
VALUES ('Campo Geral', 'Guimarães Rosa', 136, 'Novela', 1, '', 'Global', 'A infância é o tempo de descobertas. É a fase da vida em que o ser humano recebe e retribui os sentimentos à sua volta com maior vigor e integridade. Com Miguilim, menino que protagoniza esta novela de João Guimarães Rosa, não é diferente.', 1);

INSERT INTO livro (titulo, autor, paginas, genero, nacional, capa, editora, descricao, id_usuario)
VALUES ('Bird Box', 'Josh Malerman', 320, 'suspense', 0, '', 'Intrinseca', 'Quatro anos depois de as mortes terem começado, há poucos sobreviventes em Michigan. Malorie e seus dois filhos pequenos estão entre eles. O trio faz parte do grupo que tenta resistir em um mundo no qual abrir os olhos pode ser fatal.', 1);

INSERT INTO livro (titulo, autor, paginas, genero, nacional, capa, editora, descricao, id_usuario)
VALUES ('A Divina Comédia', 'Dante Alighieri', 240, 'comedia', 0, '', 'Principis', 'A Divina Comédia é um poema clássico da literatura italiana e mundial escrito por Dante Alighieri no século XIV e dividido em três partes: o Inferno, o Purgatório e o Paraíso. São cem cantos protagonizados pelo autor acompanhado do poeta romano Virgílio.', 1);